import React, { useState, useEffect } from 'react';
import './HomePage.css';

const HomePage = () => {
  const [registrationMessage, setRegistrationMessage] = useState('');

  useEffect(() => {
    const message = sessionStorage.getItem('registrationMessage');
    if (message) {
      setRegistrationMessage(message);
      sessionStorage.removeItem('registrationMessage'); // Remove message after displaying
    }
  }, []);

  return (
    <div className="home-page">
      <h1>Welcome to Hostel Mess Management</h1>

      {/* Registration Section */}
      <div className="registration-container">
        <h3>Student Registration</h3>
        <button>Register</button>
      </div>

      {/* Display success message if available */}
      {registrationMessage && <p className="success-message">{registrationMessage}</p>}

      {/* Login Section */}
      <div className="login-container">
        <h3>Login</h3>
        <div className="dropdown">
          <button className="dropdown-btn">Login</button>
          <div className="dropdown-menu">
            <ul>
              <li><a href="/login">Student Dashboard</a></li>
              <li><a href="/admin/login">Admin Dashboard</a></li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
