import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const location = useLocation();
  const [dropdownActive, setDropdownActive] = useState(false);
  const dropdownRef = useRef(null);

  // Toggle dropdown visibility
  const toggleDropdown = (event) => {
    event.stopPropagation();  // Prevent this click from propagating to the document
    setDropdownActive((prev) => !prev); 
    console.log("Dropdown button clicked");
  };

  // Close dropdown if clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      // Delay closing dropdown for the rendering time of the dropdown
      setTimeout(() => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
          setDropdownActive(false);
          console.log("Mouse clicked outside, closing dropdown");
        }
      }, 100); // Delay of 100ms
    };

    // Only attach the event listener if dropdown is active
    if (dropdownActive) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    // Clean up the event listener when the component is unmounted or dropdownActive changes
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [dropdownActive]); // This will run when dropdownActive changes

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Hostel Mess Management</Link>
      </div>

      <ul className="navbar-links">
        {location.pathname === '/' && (
          <>
            <li><Link to="/register">Register</Link></li>
            <li className="dropdown" ref={dropdownRef}>
              <button onClick={toggleDropdown} className="dropdown-button">Login</button>
              {dropdownActive && (
                <div className="dropdown-menu">
                  <Link to="/login/student" onClick={() => setDropdownActive(false)}>Student Dashboard</Link>
                  <Link to="/login/admin" onClick={() => setDropdownActive(false)}>Admin Dashboard</Link>
                </div>
              )}
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
