import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuthContext } from '../../context/authContext'; // Correct path to authContext.js
import './studentdashboard.css'; // Correct path for the CSS file
import Calendar from './Calendar';  // Import the Calendar component

const StudentDashboard = () => {
  const { user } = useAuthContext(); // Use AuthContext to check if the user is logged in
  const navigate = useNavigate(); // Initialize the navigate function

  useEffect(() => {
    if (!user) {
      navigate('/login'); // Redirect to login page if user is not authenticated
    }
  }, [user, navigate]);

  return (
    <div className="dashboard-container">
      {/* Fixed Header */}
      <header className="dashboard-header">
        <h1>Student Dashboard</h1>
      </header>

      {/* Main Content */}
      <main className="dashboard-content">
        <div className="left-column">
          {/* Notice Board */}
          <div className="dashboard-section notice-board">
            <h4>Notice Board</h4>
            <p>No new updates.</p>
          </div>

          {/* Calendar */}
          <div className="dashboard-section calendar">
            <h4>Calendar</h4>
            <Calendar />
          </div>
        </div>

        <div className="center-column">
          {/* Menu */}
          <div className="dashboard-section menu-section">
            <h3>Today's Menu</h3>
            <p>Breakfast: Sandwich</p>
            <p>Lunch: Pasta</p>
            <p>Dinner: Salad</p>
          </div>

          {/* Payment Status */}
          <div className="dashboard-section payment-section">
            <h3>Payment</h3>
            <p>Mess fee status: Pending</p>
            <button type="button">Pay Now</button> {/* Payment button */}
          </div>
        </div>

        <div className="right-column">
          {/* Feedback Form */}
          <div className="dashboard-section feedback-form">
            <h4>Feedback</h4>
            <form>
              <textarea placeholder="Enter your feedback here..." rows="4" cols="30"></textarea>
              <button type="submit">Submit Feedback</button>
            </form>
          </div>
        </div>
      </main>
    </div>
  );
};

export default StudentDashboard;
