// src/components/common/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Hostel Mess Management</Link>
      </div>
      <ul className="navbar-links">
        <li><Link to="/register">Register</Link></li>
        <li className={`dropdown ${isDropdownOpen ? 'active' : ''}`} onClick={toggleDropdown}>
          <button className="dropdown-btn">Login</button>
          <div className="dropdown-menu">
            <ul>
              <li><Link to="/login">Student Dashboard</Link></li>
              <li><Link to="/admin/login">Admin Dashboard</Link></li>
            </ul>
          </div>
        </li>
        <li><Link to="/student/dashboard">Student Dashboard</Link></li>
        <li><Link to="/admin/meal-plan">Admin Dashboard</Link></li>
      </ul>
    </nav>
  );
};

export default Navbar;
