import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // to navigate back to home page after registration
import './Registration.css'; // Import CSS file for styling (if exists)

const Registration = () => {
  const [formData, setFormData] = useState({
    username: '',
    name: '',
    rollNo: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [error, setError] = useState(''); // State to store error messages
  const [success, setSuccess] = useState(''); // State to store success messages
  const navigate = useNavigate();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Reset error state before submission
    setSuccess(''); // Reset success state before submission

    const { username, name, rollNo, email, password, confirmPassword } = formData;

    // Simple validation for required fields and password matching
    if (!username || !name || !rollNo || !email || !password || !confirmPassword) {
      setError('Please fill in all fields');
      return;
    }

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      // Simulate the registration API call (you can call your API function here)
      // await registerUser(formData); // Uncomment and replace with your actual registration function
      setSuccess('Registration done successfully! Now login!');

      // Redirect to homepage after 2 seconds with success message
      setTimeout(() => {
        navigate('/'); // Redirect to the home page
      }, 2000);
    } catch (err) {
      setError('Registration failed. Please try again.');
    }
  };

  return (
    <div className="registration-container">
      <h2>Student Registration</h2>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="rollNo"
          placeholder="Roll No."
          value={formData.rollNo}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirm Password"
          value={formData.confirmPassword}
          onChange={handleChange}
          required
        />
        <button type="submit">Register</button>
      </form>

      {error && <p className="error-message">{error}</p>} {/* Display error message */}
      {success && <p className="success-message">{success}</p>} {/* Display success message */}
    </div>
  );
};

export default Registration;
