import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';  // useLocation to get the current route
import './Navbar.css';

const Navbar = () => {
  const location = useLocation();  // Get current route
  const [dropdownActive, setDropdownActive] = useState(false);

  const toggleDropdown = () => {
    setDropdownActive(!dropdownActive);
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Hostel Mess Management</Link>
      </div>
      <ul className="navbar-links">
        {location.pathname === '/' ? (  // Only show Register and Login on the homepage
          <>
            <li><Link to="/register">Register</Link></li>
            <li><Link to="/login">Login</Link></li>
          </>
        ) : (  // Hide Register and Login when on other pages
          <>
            <li><Link to="/student/dashboard">Student Dashboard</Link></li>
            <li><Link to="/admin/meal-plan">Admin Dashboard</Link></li>
          </>
        )}
      </ul>

      {/* Login Dropdown */}
      {location.pathname === '/' && (
        <div className="dropdown" onClick={toggleDropdown}>
          <button className="dropdown-btn">Login</button>
          <div className={`dropdown-menu ${dropdownActive ? 'active' : ''}`}>
            <ul>
              <li><Link to="/login">Student Dashboard</Link></li>
              <li><Link to="/admin/login">Admin Dashboard</Link></li>
            </ul>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
