// src/components/StudentDashboard/studentdashboard.js
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate from react-router-dom
import { useAuthContext } from '../authContext';  // Correct path to authContext.js
import './studentdashboard.css';  // Import the CSS file

const studentdashboard = () => {
  const { user } = useAuthContext(); // Use AuthContext to check if the user is logged in
  const navigate = useNavigate();  // Initialize the navigate function

  useEffect(() => {
    if (!user) {
      navigate('/login'); // Redirect to login page if user is not authenticated
    }
  }, [user, navigate]);

  return (
    <div className="student-dashboard">
      <div className="header">Welcome to the Student Dashboard</div>

      <div className="menu-section">
        <div className="menu-item">
          <h3>Today's Menu</h3>
          <p>Breakfast: Sandwich</p>
          <p>Lunch: Pasta</p>
          <p>Dinner: Salad</p>
        </div>
        <div className="menu-item">
          <h3>Tomorrow's Menu</h3>
          <p>Breakfast: Pancakes</p>
          <p>Lunch: Pizza</p>
          <p>Dinner: Soup</p>
        </div>
      </div>

      <div className="notice-board">
        <h4>Notice Board</h4>
        <p>No new updates.</p>
      </div>

      <div className="calendar">
        <h4>Calendar</h4>
        <p>Here you can view your academic schedule.</p>
      </div>
    </div>
  );
};

export default studentdashboard;
