import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const location = useLocation();
  const [dropdownActive, setDropdownActive] = useState(false);
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    console.log("Dropdown button clicked"); // Debugging log
    setDropdownActive((prev) => !prev);
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Hostel Mess Management</Link>
      </div>

      <ul className="navbar-links">
        {location.pathname === '/' && (
          <>
            <li><Link to="/register">Register</Link></li>
            <li className="dropdown" ref={dropdownRef}>
              <button onClick={toggleDropdown} className="dropdown-button">Login</button>
              {dropdownActive && (
                <>
                  {console.log("Dropdown rendered")} {/* Debugging log */}
                  <div className="dropdown-menu">
                    <Link to="/login/student" onClick={() => setDropdownActive(false)}>Student Dashboard</Link>
                    <Link to="/login/admin" onClick={() => setDropdownActive(false)}>Admin Dashboard</Link>
                  </div>
                </>
              )}
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
