// src/components/common/PrivateRoute.js
import React from 'react';
import { Route, Routes, Navigate } from 'react-router-dom'; // Import Navigate and Routes
import { useAuthContext } from '../../context/authContext'; // Make sure this matches the path of your Auth context

const PrivateRoute = ({ component: Component, ...rest }) => {
  const { isAuthenticated } = useAuthContext(); // Assumes `useAuthContext` provides `isAuthenticated`

  return (
    <Routes> {/* Wrap the Route in Routes */}
      <Route
        {...rest}
        element={
          isAuthenticated ? (
            <Component {...rest} /> // Pass rest props to the Component if needed
          ) : (
            <Navigate to="/login" /> // Use Navigate for redirecting
          )
        }
      />
    </Routes>
  );
};

export default PrivateRoute;
