// src/components/AdminDashboard/AttendanceReport.js
import React, { useEffect, useState } from 'react';
import { getAttendanceReport } from '../../services/api';

const AttendanceReport = () => {
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const fetchAttendanceReport = async () => {
      try {
        const data = await getAttendanceReport();
        setAttendance(data);
      } catch (err) {
        setError('Failed to fetch attendance report');
      } finally {
        setLoading(false);
      }
    };
    fetchAttendanceReport();
  }, []);

  if (loading) return <p>Loading attendance report...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Attendance Report</h2>
      <table>
        <thead>
          <tr>
            <th>Student</th>
            <th>Attendance</th>
          </tr>
        </thead>
        <tbody>
          {attendance.map((record, index) => (
            <tr key={index}>
              <td>{record.student}</td>
              <td>{record.daysAttended}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {attendance.length === 0 && <p>No attendance records found.</p>} {/* Handle empty state */}
    </div>
  );
};

export default AttendanceReport;
