import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const location = useLocation();

  const handleDropdownToggle = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const renderLoginDropdown = () => {
    if (isDropdownOpen) {
      return (
        <ul className="dropdown-menu">
          <li>
            <Link to="/student/dashboard">Student Dashboard</Link>
          </li>
          <li>
            <Link to="/admin/dashboard">Admin Dashboard</Link>
          </li>
        </ul>
      );
    }
    return null;
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Hostel Mess Management</Link>
      </div>
      <ul className="navbar-links">
        {/* Conditionally render Register and Login based on current route */}
        {location.pathname === '/' && (
          <>
            <li><Link to="/register">Register</Link></li>
            <li>
              <button onClick={handleDropdownToggle} className="login-btn">
                Login
              </button>
              {renderLoginDropdown()}
            </li>
          </>
        )}
        {/* You can hide other links like Student Dashboard and Admin Dashboard when not logged in */}
      </ul>
    </nav>
  );
};

export default Navbar;
