import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const [isRegistered, setIsRegistered] = useState(false); // Track registration status
  const navigate = useNavigate();

  // Handle registration logic (mockup)
  const handleRegistration = () => {
    setIsRegistered(true);
    setTimeout(() => {
      navigate('/'); // Redirect to homepage after successful registration
    }, 2000);
  };

  return (
    <div className="home-page">
      <h1>Welcome to Hostel Mess Management</h1>

      {/* Registration Section */}
      {!isRegistered ? (
        <div className="registration-container">
          <h3>Student Registration</h3>
          <button onClick={handleRegistration}>Register</button>
        </div>
      ) : (
        <div className="registration-success">
          <p>Registration done successfully! Now login!</p>
        </div>
      )}

      {/* Login Section */}
      <div className="login-container">
        <h3>Login</h3>
        <div className="dropdown">
          <button className="dropdown-btn">Login</button>
          <div className="dropdown-menu">
            <ul>
              <li><a href="/login">Student Dashboard</a></li>
              <li><a href="/admin/login">Admin Dashboard</a></li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
